import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [mealData, setMealData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("http://localhost:3000/yemekler");
      const data = await response.json();

      if (!data || !data.tarih) return;

      // frontend'de mevcut ay listesini tam çekip index belirle
      const allResponse = await fetch("http://localhost:3000/yemekler?date=all"); // bu endpointi sen oluşturmadınsa söyle yapayım
      const allData = await allResponse.json();

      const index = allData.findIndex(d => d.tarih === data.tarih);
      setMealData(allData);
      setCurrentIndex(index !== -1 ? index : 0);
    };

    fetchData();
  }, []);

  const handleNext = () => {
    if (currentIndex < mealData.length - 1) setCurrentIndex(currentIndex + 1);
  };

  const handlePrev = () => {
    if (currentIndex > 0) setCurrentIndex(currentIndex - 1);
  };

  const handleToday = () => {
    const today = new Date();
    const formatted = today.toLocaleDateString("tr-TR").padStart(10, "0").replace(/\//g, ".");

    const index = mealData.findIndex((item) => item.tarih === formatted);
    if (index !== -1) setCurrentIndex(index);
  };

  const currentMeal = mealData[currentIndex];

  return (
    <div className="container">
      {currentMeal ? (
        <>
          <div className="date">{currentMeal.tarih}</div>
          <div className="card">
            {currentMeal.yemekler.map((item, i) => (
              <div className="meal-item" key={i}>{item}</div>
            ))}
          </div>
          <div className="calories">{currentMeal.kalori}</div>
          <div className="button-group">
            <button onClick={handlePrev} disabled={currentIndex === 0}>← Önceki</button>
            <button onClick={handleToday}>Bugün</button>
            <button onClick={handleNext} disabled={currentIndex === mealData.length - 1}>Sonraki →</button>
          </div>
        </>
      ) : (
        <p>Yükleniyor...</p>
      )}
      <div className="note">Veriler: İstanbul Üniversitesi Yemek Listesi</div>
    </div>
  );
}

export default App;
