import React, { useEffect, useState } from "react";
import mealData from "./data";
import "./App.css";

function App() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    if (currentIndex < mealData.length - 1) setCurrentIndex(currentIndex + 1);
  };

  const handlePrev = () => {
    if (currentIndex > 0) setCurrentIndex(currentIndex - 1);
  };

  const handleToday = () => {
    const today = new Date();
    const formatted = today.toLocaleDateString("tr-TR").padStart(10, "0").replace(/\//g, ".");
    const index = mealData.findIndex((item) => item.date === formatted);
    if (index !== -1) setCurrentIndex(index);
  };

  const currentMeal = mealData[currentIndex];

  return (
    <div className="container">
      {currentMeal ? (
        <>
          <div className="date">{currentMeal.date}</div>
          <div className="card">
            {currentMeal.items.slice(0, -1).map((item, i) => (
              <div className="meal-item" key={i}>{item}</div>
            ))}
          </div>
          <div className="calories">{currentMeal.items[currentMeal.items.length - 1]}</div>
          <div className="button-group">
            <button onClick={handlePrev} disabled={currentIndex === 0}>← Önceki</button>
            <button onClick={handleToday}>Bugün</button>
            <button onClick={handleNext} disabled={currentIndex === mealData.length - 1}>Sonraki →</button>
          </div>
        </>
      ) : (
        <p>Yükleniyor...</p>
      )}
      <div className="note">Veriler: İstanbul Üniversitesi Yemek Listesi</div>
    </div>
  );
}

export default App;
