import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [currentDate, setCurrentDate] = useState(() => {
    const today = new Date();
    return today.toISOString().split("T")[0]; // YYYY-MM-DD formatı
  });
  const [mealData, setMealData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:3000/yemekler"); // Render backend URL
        const data = await response.json();
        console.log("API'den gelen veri:", data);

        if (data && data.tarih && data.yemekler && data.kalori) {
          const formatted = {
            date: data.tarih,
            items: data.yemekler,
            cal: data.kalori
          };
          setMealData([formatted]); // Dizi olarak setlenmeli çünkü currentMeal = mealData[currentIndex]
        } else {
          console.error("Veri beklenen formatta değil:", data);
        }
      } catch (error) {
        console.error("Veri çekme hatası:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handlePrev = () => {
    setCurrentIndex((prev) => (prev > 0 ? prev - 1 : prev));
  };

  const handleNext = () => {
    setCurrentIndex((prev) => (prev < mealData.length - 1 ? prev + 1 : prev));
  };

  const currentMeal = mealData[currentIndex];

  return (
    <div className="container">
      {loading ? (
        <p>Yükleniyor...</p>
      ) : currentMeal ? (
        <>
          <div className="date">{currentMeal.date}</div>
          <div className="card">
            {currentMeal.items.map((item, index) => (
              <div className="meal-item" key={index}>
                {item}
              </div>
            ))}
          </div>
          <div className="button-group">
            <button onClick={handlePrev} disabled={currentIndex === 0}>
              ← Önceki
            </button>
            <button
              onClick={handleNext}
              disabled={currentIndex === mealData.length - 1}
            >
              Sonraki →
            </button>
          </div>
        </>
      ) : (
        <p>Veri bulunamadı</p>
      )}
      <div className="note">Veriler: İstanbul Üniversitesi Yemek Listesi</div>
    </div>
  );
}

export default App;
