import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [currentDate, setCurrentDate] = useState(getTodayFormatted()); // Bugünün tarihi ile başla
const [mealData, setMealData] = useState(null);
const [loading, setLoading] = useState(true);

function getTodayFormatted() {
  const today = new Date();
  return today.toLocaleDateString("tr-TR");
}


useEffect(() => {
  const fetchData = async () => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:3000/yemekler?date=${currentDate}`);
      const data = await response.json();
      setMealData(data);
    } catch (error) {
      console.error("Veri çekme hatası:", error);
    } finally {
      setLoading(false);
    }
  };

  fetchData();
}, [currentDate]); // currentDate değişince veriyi tekrar çeksin

  

const handleNext = () => {
  const [day, month, year] = currentDate.split(".").map(Number);
  const newDate = new Date(year, month - 1, day + 1);
  const formatted = newDate.toLocaleDateString("tr-TR");
  setCurrentDate(formatted);
};

const handlePrev = () => {
  const [day, month, year] = currentDate.split(".").map(Number);
  const newDate = new Date(year, month - 1, day - 1);
  const formatted = newDate.toLocaleDateString("tr-TR");
  setCurrentDate(formatted);
};


